Function Discover-BackupVault { param($obj, $ResourceGroup, $Subscription)
#$obj #$obj.properties
$objInstance = $discoveryData.CreateClassInstance("$MPElement[Name='QND.OMS.Backup.Vault']$")
$objInstance.AddProperty("$MPElement[Name='OMS!QND.OMS.Azure.Subscription']/Id$", $subscription)
$objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Base']/Id$", $obj.id) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Base']/Type$", $obj.type)
$objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/Name$", $obj.name) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/ResourceGroup$", $ResourceGroup) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/Subscription$", $Subscription) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/Location$", $obj.location)
if($obj.properties.sku) {$sku=$obj.properties.sku.name} else {$sku=$obj.sku} if([String]::IsNullOrEmpty($sku)){$sku=''} $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/Sku$", $sku)
$objInstance.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", $obj.name) $discoveryData.AddInstance($objInstance) }
