Function Discover-BackupContainer { param($obj, $subscription, $vaultName)
#$obj
try { if($obj.uniqueName) { $containerType=('{0}/{1}' -f $obj.ContainerType, $obj.properties.customerType ) $id=$obj.UniqueName $name=$obj.UniqueName $type='microsoft.backup/MachineContainer' $displayName=$obj.properties.friendlyName } else { $containerType=$obj.properties.containerType $id=$obj.Id $name=$obj.Name $type=$obj.type $displayName=$obj.properties.friendlyName }
$objInstance = $discoveryData.CreateClassInstance("$MPElement[Name='QND.OMS.Backup.Container']$") $objInstance.AddProperty("$MPElement[Name='OMS!QND.OMS.Azure.Subscription']/Id$", $subscription) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/Name$", $vaultName)
$objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Base']/Id$", $id) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Base']/Type$", $type)
$objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Container']/Name$", $name) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Container']/ContainerType$", $containerType)
$objInstance.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", $displayName) $discoveryData.AddInstance($objInstance) } catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ('Error disocvering backup container {1} in vault {0} - {2}' -f $name, $Id, $Error[0]) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }
}
