Function Discover-BackupProtectedItem { param($obj, $subscription, $vaultName, $container)
#$obj try { if ($obj) { $objInstance = $discoveryData.CreateClassInstance("$MPElement[Name='QND.OMS.Backup.ProtectedItem']$") $objInstance.AddProperty("$MPElement[Name='OMS!QND.OMS.Azure.Subscription']/Id$", $subscription) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Vault']/Name$", $vaultName) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Container']/Name$", $container)
$objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Base']/Id$", $obj.id) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Base']/Type$", $obj.type)
$objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.ProtectedItem']/Name$", $obj.name) $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.ProtectedItem']/ItemType$", $obj.properties.itemType)
#Write-Host $obj.properties $obj.properties.containerId -match '(^.*protectedItems\/)' | Out-Null $policyName=$obj.properties.protectionPolicyId.Replace($matches[0],'') $objInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.ProtectedItem']/PolicyName$", $policyName)
$objInstance.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", $obj.properties.friendlyName) $discoveryData.AddInstance($objInstance) } } catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ('Error disocvering backup Item {2} in vault {0}\{1} - {3}' -f $vaultName, $container, $obj.Id, $Error[0]) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }
} #Start by setting up API object. $P_TraceLevel = $TRACE_VERBOSE $g_Api = New-Object -comObject 'MOM.ScriptAPI' #$g_RegistryStatePath = "HKLM\" + $g_API.GetScriptStateKeyPath($SCRIPT_NAME)
$dtStart = Get-Date $P_TraceLevel = $traceLevel Log-Params $MyInvocation
try { $ResPath = (get-itemproperty -path 'HKLM:\system\currentcontrolset\services\healthservice\Parameters' -Name 'State Directory').'State Directory' + '\Resources' if(Test-Path $ResPath) { $module = @(get-childitem -path $ResPath -Name OMSSearch.psm1 -Recurse)[0] } if($module) { $OMSSearchModule = "$ResPath\$module"} else {$OMSSearchModule = '.\OMSSearch.psm1'} if(! (Get-MOdule -Name OMSSearch)) { If (Test-Path $OMSSearchModule) {Import-Module $OMSSearchModule} else {Throw [System.DllNotFoundException] 'OMSSearch.psm1 not found'} }
#now load the AAD client resource. The module I'm using assumes the assembly is in the same directory of the module, but OpsMgr resource deployment uses 2 different folders
$AssemblyName = 'Microsoft.IdentityModel.Clients.ActiveDirectory' $AssemblyVersion = "2.14.0.0" $AssemblyPublicKey = "31bf3856ad364e35"
$DLLPath = "$ResPath\"+@(get-childitem -path $ResPath -Name "$($AssemblyName).dll" -Recurse)[0] If (!([AppDomain]::CurrentDomain.GetAssemblies() |Where-Object { $_.FullName -eq "$AssemblyName, Version=$AssemblyVersion, Culture=neutral, PublicKeyToken=$AssemblyPublicKey"})) { Log-Event $INFO_EVENT_ID $EVENT_TYPE_INFO ("Loading Assembly $AssemblyName") $TRACE_VERBOSE Try { [Void][System.Reflection.Assembly]::LoadFrom($DLLPath) } Catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_ERROR ("Unable to load $DLLPath. $Error") $TRACE_ERROR exit 1 } }
if (!(get-command -Module OMSSearch -Name Get-AADToken -ErrorAction SilentlyContinue)) { Log-Event $START_EVENT_ID $EVENT_TYPE_WARNING ("Get-AADToken Commandlet doesn't exist.") $TRACE_WARNING Throw-KeepDiscoveryInfo exit 1 } $token = Get-AADToken -TenantADName $TenantADName -Username $Username -Password $Password if (! $token) {Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_ERROR ("Get-AADToken canno authenticate user $username " + $Error) $TRACE_ERROR; exit 1;} } catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_ERROR ("Main " + $Error) $TRACE_ERROR exit 1 }
try { $exclusions=@() if (! ([String]::IsNullOrEmpty($ExcludedVaults))) {$exclusions = $ExcludedVaults.Split(',')}
$discoveryData = $g_api.CreateDiscoveryData(0, $sourceId, $managedEntityId) #first discover the accounts $uri='https://management.azure.com/subscriptions/{0}/resources?api-version=2014-04-01-preview&$filter=((resourceType eq ''microsoft.backup/BackupVault'') or (resourceType eq ''Microsoft.Backup/BackupVault''))' -f $subscriptionId $jres = Invoke-ARMGet -Token $token -Uri $uri if(! $jres) {Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_ERROR ("Invoke-ARMGet failed $uri " + $Error) $TRACE_ERROR; exit 1;} foreach($vault in $jres.Value) { if ($exclusions -inotcontains $vault.Name) { #get vault details Log-Event -eventID $INFO_EVENT_ID -eventType $EVENT_TYPE_INFORMATION -msg ('Discovering Backup vault {0}' -f $vault.Name) -level $TRACE_VERBOSE try { $vault.Id -match '(?<=(resourceGroups\/))(?<Group>.*)(?=(\/providers))' | Out-Null $ResourceGroup = $matches.Group $uri='https://management.azure.com{0}?api-version=2015-03-15' -f $vault.Id $vaultDet = Invoke-ARMGet -Token $token -Uri $uri Discover-BackupVault -obj $vaultDet -ResourceGroup $ResourceGroup -Subscription $SubscriptionId } catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ('Error disocverying vault {0} - {1}' -f $vault.name, $Error[0]) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); continue; } try { #now discover the containers for this account $uri='https://management.azure.com{0}/backupContainers?&api-version=2015-03-15' -f $vault.Id $containers = Invoke-ARMGet -Token $token -Uri $uri foreach($container in $containers.Value) { Log-Event -eventID $INFO_EVENT_ID -eventType $EVENT_TYPE_INFORMATION -msg ('Discovering container {1} in backup vault {0}' -f $vault.Name, $container.UniqueName) -level $TRACE_VERBOSE Discover-BackupContainer -obj $container -subscription $SubscriptionId -vaultName $vault.Name } $uri='https://management.azure.com{0}/containers?&api-version=2015-03-15' -f $vault.Id $containers = Invoke-ARMGet -Token $token -Uri $uri $uri='https://management.azure.com{0}/protectedItems?api-version=2014-09-01' -f $vault.Id $items = @((Invoke-ARMGet -Token $token -Uri $uri).Value) foreach($container in $containers.Value) { if ($container.properties.healthStatus -ieq 'Deleted') {continue} Log-Event -eventID $INFO_EVENT_ID -eventType $EVENT_TYPE_INFORMATION -msg ('Discovering container {1} in backup vault {0}' -f $vault.Name, $container.Name) -level $TRACE_VERBOSE Discover-BackupContainer -obj $container -subscription $SubscriptionId -vaultName $vault.Name #now disocver protcted items try { #$uri='https://management.azure.com{0}/containers?&api-version=2015-03-15' -f $vault.Id #$containerId='{0}/Items/{1}' -f ($container.Id).Replace('containers','registeredContainers'), $container.Name #$uri='https://management.azure.com{0}/protectedItems?api-version=2014-09-01&$filter=(containerId -eq ''{1}'')' -f $vault.Id, $containerId $containerId='{0}/protectedItems/{1}' -f ($container.Id).Replace('containers','registeredContainers'), $container.Name $cItems = $items | where {$_.properties.containerId -eq $containerId} foreach($item in $cItems) { Log-Event -eventID $INFO_EVENT_ID -eventType $EVENT_TYPE_INFORMATION -msg ('Discovering backup item {2} in path {0}/{1}' -f $vault.Name, $container.Name, $item.Name) -level $TRACE_VERBOSE Discover-BackupProtectedItem -obj $item -subscription $SubscriptionId -vaultName $vault.Name -container $container.Name }
} catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ('Error discoverying backup item for {0}/{1} - {2}' -f $vault.Name, $container.Name, $Error[0]) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); continue; }
}
} catch { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ('Error disocverying backup containers for vault {0} - {1}' -f $vault.name, $Error[0]) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); continue; } } else {Log-Event -eventID $INFO_EVENT_ID -eventType $EVENT_TYPE_INFORMATION -msg ('Backup Vault {0} filtreed out' -f $vault.Name) -level $TRACE_INFO} }
$discoveryData If ($traceLevel -eq $TRACE_DEBUG) { #just for debug proposes when launched from command line does nothing when run inside OpsMgr Agent #it breaks in exception when run insde OpsMgr and POSH IDE $g_API.Return($discoveryData) } #get all the subscriptions Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $Error) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }

