Function Throw-KeepDiscoveryInfo { param($SourceId, $ManagedEntityId) $oDiscoveryData = $g_API.CreateDiscoveryData(0,$SourceId,$ManagedEntityId) #Instead of Snapshot discovery, submit Incremental discovery data $oDiscoveryData.IsSnapshot = $false Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING "Exiting with null non snapshot discovery data" $TRACE_INFO $oDiscoveryData If($traceLevel -eq $TRACE_DEBUG) { #just for debug proposes when launched from command line does nothing when run inside OpsMgr Agent $g_API.Return($oDiscoveryData) } }
#Start by setting up API object. $P_TraceLevel = $TRACE_VERBOSE $g_Api = New-Object -comObject 'MOM.ScriptAPI' #$g_RegistryStatePath = "HKLM\" + $g_API.GetScriptStateKeyPath($SCRIPT_NAME)
$dtStart = Get-Date #if we use named parameters then $Args is always 0, so the erroc hecking must be done differently
#v2 #if ($PsBoundParameters.Count -ne $SCRIPT_ARGS) #at the momento we must do specific command line validation or use the syntax #Param([string]$a=$(throw 'My exception - parameter a needed') # If ($Args.Count -ne $SCRIPT_ARGS) # { # Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_ERROR "called without proper arguments and was not executed." $TRACE_ERROR # exit -1 # }
$P_TraceLevel = $traceLevel #same story for dumping the command line #manul stuff today in v2 we could use $PSBoundParameters Log-Params ([string]$traceLevel + " " + $computerName + " " + $sourceID + " " + $ManagedEntityId) try { if (! (Get-Module -Name MSOnlineBackup)) {Import-Module MSOnlineBackup} $policies = Get-OBPolicy | Where {$_.State -eq 'Existing'} if ($policies) { $discoveryData = $g_api.CreateDiscoveryData(0, $sourceId, $managedEntityId) foreach ($pol in $policies) { $PInstance = $discoveryData.CreateClassInstance("$MPElement[Name='QND.OMS.Backup.Agent.Policy']$") $PInstance.AddProperty("$MPElement[Name='Windows!Microsoft.Windows.Computer']/PrincipalName$", $computerName) $PInstance.AddProperty("$MPElement[Name='System!System.Entity']/DisplayName$", "OB Policy ($($pol.PolicyName))") $PInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Agent.Policy']/PolicyName$", $pol.PolicyName) $PInstance.AddProperty("$MPElement[Name='QND.OMS.Backup.Agent.Policy']/RetentionDays$", $pol.RetentionPolicy.RetentionDays) $discoveryData.AddInstance($PInstance) } $discoveryData } else { Throw-EmptyDiscovery $sourceID $ManagedEntityId }
If ($traceLevel -eq $TRACE_DEBUG) { #just for debug proposes when launched from command line does nothing when run inside OpsMgr Agent $g_API.Return($discoveryData) } Log-Event $STOP_EVENT_ID $EVENT_TYPE_SUCCESS ("has completed successfully in " + ((Get-Date)- ($dtstart)).TotalSeconds + " seconds.") $TRACE_INFO } Catch [Exception] { Log-Event $FAILURE_EVENT_ID $EVENT_TYPE_WARNING ("Main " + $Error) $TRACE_WARNING write-Verbose $("TRAPPED: " + $_.Exception.GetType().FullName); Write-Verbose $("TRAPPED: " + $_.Exception.Message); }

